import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Modal from '@mui/material/Modal';
import { styled } from '@mui/system';

const cardStyle = {
  maxWidth: 345,
};

const modalStyle = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  p: 2,
};

const ModalContent = styled('div')(modalStyle);

const PokemonCard = ({ name, image, types, data, abilities, stats }) => {
  const [open, setOpen] = useState(false);
  const [pokeData, setPokeData] = useState([]);
  const [loading, setLoading] = useState(true);

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const typeHandle = () => {
    return types[1] ? `${types[0].type.name} ${types[1].type.name}` : types[0].type.name;
  };

  const fetchPokemonData = async () => {
    try {
      setLoading(true);
      const res = await axios.get("https://pokeapi.co/api/v2/pokemon/");
      setPokeData(res.data.results);
      setLoading(false);
    } catch (error) {
      console.error('Error fetching Pokemon data:', error);
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPokemonData();
  }, []);

  return (
    <>
      <Card sx={cardStyle}>
        <CardMedia component="img" height="240" image={image} alt={name} />
        <CardContent>
          <Box display="flex" justifyContent="space-between">
            <Typography gutterBottom variant="h6" component="div">
              {name}
            </Typography>
            <Typography gutterBottom variant="h6" component="div">
              {typeHandle()}
            </Typography>
          </Box>
        </CardContent>
        <CardActions>
          <Button onClick={handleOpen} size="small" color="primary">
            More Details
          </Button>
        </CardActions>
      </Card>

      <Modal open={open} onClose={handleClose}>
        <ModalContent sx={modalStyle}>
          <CardContent>
            <Typography variant="h4" component="div">
              {name}
            </Typography>
            <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/${data.id}.svg`} alt="" />
            <div className="abilities">
              {abilities.map((poke, index) => (
                <div key={index} className="group">
                  <Typography variant="h6">{poke.ability.name}</Typography>
                </div>
              ))}
            </div>
            <div className="base-stat">
              {stats.map((poke, index) => (
                <div key={index}>
                  <Typography variant="h6">{poke.stat.name}: {poke.base_stat}</Typography>
                </div>
              ))}
            </div>
          </CardContent>
          <Button onClick={handleClose}>Close</Button>
        </ModalContent>
      </Modal>
    </>
  );
};

export default PokemonCard;

