import React from "react";
import Imagecard from "./Imagecard";
import Pokemoninfo from "./Pokemoninfo";
import axios from "axios";
import { useState, useEffect } from "react";
import Button from "@mui/material/Button";
import Container from "@mui/material/Container";
import Grid from "@mui/material/Grid";

const Main = () => {
  const [pokeData, setPokeData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [url, setUrl] = useState("https://pokeapi.co/api/v2/pokemon/");
  const [nextUrl, setNextUrl] = useState();
  const [prevUrl, setPrevUrl] = useState();
  const [pokeDex, setPokeDex] = useState();

  const [pokemons, setPokemons] = useState([]);

  const pokeFun = async () => {
    setLoading(true);
    try {
      const res = await axios.get(url);
      setNextUrl(res.data.next);
      setPrevUrl(res.data.previous);
      await getPokemon(res.data.results);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching data:", error);
      setLoading(false);
    }
  };

  const getPokemon = async (res) => {
    try {
      const promises = res.map(async (item) => {
        const result = await axios.get(item.url);
        return result.data;
      });

      const pokemonData = await Promise.all(promises);

      setPokeData((prevData) => {
        const newData = [...prevData, ...pokemonData];
        newData.sort((a, b) => a.id - b.id);
        return newData;
      });
    } catch (error) {
      console.error("Error fetching Pokemon data:", error);
    }
  };

  useEffect(() => {
    pokeFun();
  }, [url]);

  const pokemonfilter =(name)=>{
    let filteredpokemon = []
    // console.log(name)
    if(name===""){
      getPokemon()
    }
    for(let i in pokemons)
    {
      if(pokemons[i].data.name.includes(name)){
        filteredpokemon.push(pokemons[i])
      }
    }
    setPokemons(filteredpokemon)
  
  }
  return (
    <>
         <Searchnav pokemonfilter ={pokemonfilter} />

      
<Container>
  <Grid container spacing={3}>
    <Grid item xs={8}>
      <Imagecard pokemon={pokeData} loading={loading} infoPokemon={(poke) => setPokeDex(poke)} />

      <div className="btn-group">
        {prevUrl && (
          <Button variant="contained" color="primary" onClick={() => {
            setPokeData([]);
            setUrl(prevUrl);
          }}>
            Previous
          </Button>
        )}

        {nextUrl && (
          <Button variant="contained" color="primary" onClick={() => {
            setPokeData([]);
            setUrl(nextUrl);
          }}>
            Next
          </Button>
        )}
      </div>
    </Grid>
    <Grid item xs={4}>
      <Pokemoninfo data={pokeDex} />
    </Grid>
  </Grid>
</Container>
    </>
   
  );
};

export default Main;

