import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Box, Container } from '@mui/system';
import Pokemoncard from '../Component/Pokemoncard';
import Searchnav from '../Component/Searchnav';
import { Grid } from '@mui/material';
import Modeldetail from '../Component/Modeldetail';

import Pagination from '@mui/material/Pagination';
import Stack from '@mui/material/Stack';

export default function Home() {
  const [pokeData,setPokeData]=useState([]);
  const [loading,setLoading]=useState(true);
  const [url,setUrl]=useState("https://pokeapi.co/api/v2/pokemon/")
  const [nextUrl,setNextUrl]=useState();
  const [prevUrl,setPrevUrl]=useState();
  const [pokeDex,setPokeDex]=useState();

  const pokeFun=async()=>{
      setLoading(true)
      const res=await axios.get(url);
      setNextUrl(res.data.next);
      setPrevUrl(res.data.previous);
      getPokemon(res.data.results)
      setLoading(false)
  }
  const getPokemons=async(res)=>{
     res.map(async(item)=>{
        const result=await axios.get(item.url)
        setPokeData(state=>{
            state=[...state,result.data]
            state.sort((a,b)=>a.id>b.id?1:-1)
            return state;
        })
     })   
  }
  useEffect(()=>{
      pokeFun();
  },[url])
 
  const [pokemons, setPokemons] = useState([]);

  useEffect(() => {
    getPokemon();
  }, []);

  const getPokemon = () => {

    let endpoints= []
    for(let i=1;i<200; i++){
      endpoints.push(`https://pokeapi.co/api/v2/pokemon/${i}/`)
    }
    console.log(endpoints)
    axios.all(endpoints.map((endpoint)=>axios.get(endpoint))).then((res)=>setPokemons(res))

    // axios.get('https://pokeapi.co/api/v2/pokemon?limit=50')
    //   .then((res) => setPokemons(res.data.results))
    //   .catch((error) => console.log(error));
  };
  const pokemonfilter =(name)=>{
    let filteredpokemon = []
    // console.log(name)
    if(name===""){
      getPokemon()
    }
    for(let i in pokemons)
    {
      if(pokemons[i].data.name.includes(name)){
        filteredpokemon.push(pokemons[i])
      }
    }
    setPokemons(filteredpokemon)
  
  }
  return (
    <>
      <Searchnav pokemonfilter ={pokemonfilter} />

      {/* <Container maxWidth="xg">
        <Grid container spacing={2}>
        {pokemons.length===0?(<Modeldetail />):(
        
        pokemons.map((pokemon, key) => (
            <Grid  key={key}  item xs={12} md={4} sm={5} lg={3}>
              <Pokemoncard name={pokemon.data.name} image={pokemon.data.sprites.front_default} types={pokemon.data.types} />
            </Grid>
          ))
        )}
        </Grid>
    <Stack spacing={2} >
      <Pagination count={60} color="primary" />
    
    </Stack>
      </Container> */}
      <><div className="container">
                <div className="left-content">
                    <Pokemoncard pokemon={pokeData} loading={loading} infoPokemon={poke=>setPokeDex(poke)}/>
                    
                    <div className="btn-group">
                        {  prevUrl && <button onClick={()=>{
                            setPokeData([])
                           setUrl(prevUrl) 
                        }}>Previous</button>}

                        { nextUrl && <button onClick={()=>{
                            setPokeData([])
                            setUrl(nextUrl)
                        }}>Next</button>}

                    </div>
                </div>
                <div className="right-content">
                   <Pokemoncard data={pokeDex}/>
                </div>
            </div></>
    </>
  );
}
