import React from "react";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import Typography from "@mui/material/Typography";
import Grid from "@mui/material/Grid";
import { useState } from "react";
import Button from "@mui/material/Button";
import Mainmodel from "./Mainmodel";

import Modal from "@mui/material/Modal";
import Box from "@mui/material/Box";

const Imagecard = ({ pokemon, loading, infoPokemon}) => {
    const [open, setOpen] = useState(false);

    const handleOpen = () => {
      setOpen(true);
    };
  
    const handleClose = () => {
      setOpen(false);
    };
  
  return (
    <>
        <Grid container spacing={2}>
      
    
      {
          pokemon.map((item) => (
            <Grid item xs={12} sm={6} md={4} lg={3} key={item.id}>
              <Card  onClick={handleOpen}>
                <CardMedia
                  component="img"
                  height="140"
                  alt={item.name}
                  src={item.sprites.front_default}
                    />
  
                <CardContent>
                  <Typography variant="h5">{item.name}</Typography>
                </CardContent>
                <Button variant="contained" onClick={handleOpen}>Open Modal</Button>
             <Mainmodel open={open} handleClose={handleClose} data={data} />
      
              </Card>
            </Grid>
          )
         )} 
      </Grid>
           <Modal
      open={open}
      onClose={handleClose}
      aria-labelledby="modal-modal-title"
      aria-describedby="modal-modal-description"
    >
      <Box sx={{ position: "absolute", top: "50%", left: "50%", transform: "translate(-50%, -50%)", width: 400, bgcolor: "background.paper", border: "2px solid #000", p: 2 }}>
        <Card>
          <CardContent>
            <Typography variant="h4" component="div">
              {data.name}
            </Typography>
            <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/${data.id}.svg`} alt="" />
            <div className="abilities">
              {data.abilities.map((poke, index) => (
                <div key={index} className="group">
                  <Typography variant="h6">{poke.ability.name}</Typography>
                </div>
              ))}
            </div>
            <div className="base-stat">
              {data.stats.map((poke, index) => (
                <div key={index}>
                  <Typography variant="h6">{poke.stat.name}: {poke.base_stat}</Typography>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
        <Button onClick={handleClose}>Close</Button>
      </Box>
    </Modal>
    </>

  );
};

export default Imagecard;


