import React, { useState } from 'react';
import Card from '@mui/material/Card';
import CardContent from '@mui/material/CardContent';
import CardMedia from '@mui/material/CardMedia';
import Typography from '@mui/material/Typography';
import { Box, Button, CardActionArea, CardActions, Modal, Fade } from '@mui/material';
import { styled } from '@mui/system';

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};

const TriggerButton = styled(Button)({
  position: 'fixed',
  bottom: 16,
  right: 16,
});

const ModalContent = styled('div')(style);

const PokemonCard = ({ name, image, types , data , abilities , stats }) => {
  const [open, setOpen] = useState(false);

  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const typeHandle = () => {
    return types[1] ? `${types[0].type.name} ${types[1].type.name}` : types[0].type.name;
  };

 

  return (
    <>
      <Card sx={{ maxWidth: 345 }}>
        <CardActionArea onClick={handleOpen}>
          <CardMedia component="img" height="240" image={image} alt={name} />
          <CardContent>
            <Box display="flex" justifyContent="space-between">
              <Typography gutterBottom variant="h6" component="div">
                {name}
              </Typography>
              <Typography gutterBottom variant="h6" component="div">
                {typeHandle()}
              </Typography>
            </Box>
          </CardContent>
          <Button onClick={handleOpen} size="small" color="primary">
            More Details
          </Button>
        </CardActionArea>
      </Card>
      <Modal
  open={open}
  onClose={handleClose}
  aria-labelledby="modal-modal-title"
  aria-describedby="modal-modal-description"
>
  <Box sx={{ position: "absolute", top: "50%", left: "50%", transform: "translate(-50%, -50%)", width: 400, bgcolor: "background.paper", border: "2px solid #000", p: 2 }}>
    <Card>
      <CardContent>
        <Typography variant="h4" component="div">
          {name}
        </Typography>
        <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/${data.id}.svg`} alt="" />
        <div className="abilities">
          {abilities.map((poke, index) => (
            <div key={index} className="group">
              <Typography variant="h6">{poke.ability.name}</Typography>
            </div>
          ))}
        </div>
        <div className="base-stat">
          {stats.map((poke, index) => (
            <div key={index}>
              <Typography variant="h6">{poke.stat.name}: {poke.base_stat}</Typography>
            </div>
          ))}
        </div>
      </CardContent>
    </Card>
    <Button onClick={handleClose}>Close</Button>
  </Box>
</Modal>

    </>
   
  );
};

export default PokemonCard;

